import 'package:flutter/material.dart';import 'package:firebase_core/firebase_core.dart';import 'firebase_options.dart';import 'pages/login_page.dart';import 'pages/admin_dashboard.dart';Future<void> main() async {  WidgetsFlutterBinding.ensureInitialized();  await Firebase.initializeApp(    options: DefaultFirebaseOptions.currentPlatform,  );  runApp(const FitPointsAdminApp());}class FitPointsAdminApp extends StatelessWidget {  const FitPointsAdminApp({super.key});  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'FitPoints Admin',      debugShowCheckedModeBanner: false,      theme: ThemeData(        useMaterial3: true,        colorScheme: ColorScheme.fromSeed(          seedColor: const Color(0xFF2DD14F),          brightness: Brightness.light,        ),        fontFamily: 'Roboto',      ),      initialRoute: '/login',      routes: {        '/login': (_) => const AdminLoginPage(),        '/dashboard': (_) => const AdminDashboardPage(),      },    );  }}